import { React, useContext,useEffect } from "react";
import WatchList from "./WatchList";
import { prodactsConext } from "../context/contextData";
import { getReq, putReq, deleteReq, auth } from "../api/rest-helper";

const WatchApi = () => {
  const { user, watch, dispatch } = useContext(prodactsConext);


  useEffect(() => {
  oneTime()
}, [])
  const oneTime = () => {
    user.user.likes.map(userId => getReq(userId).then(response => {
    console.log("watch", response.data)
    dispatch({type:"GET_WATCH_LOCAL", payload: response.data})
  }))
  }

  console.log("watch List", watch)

  return (
    <div>
      <h1 className="text-center">Watch List</h1>
      {watch.length ? (
        <div className="container">
          <div className="row">
            {watch.map((el) => {
              return (
                <WatchList
                  key={el._id}
                  product={el}
                  dispatch={dispatch}
                  user={user}
                />
              );
            })}
          </div>
        </div>
      ) : (
        <h3 className="text-center">Watch List is Empty</h3>
      )}
    </div>
  );
};

export default WatchApi;
