import { React,useReducer } from "react";
import { Link } from "react-router-dom";
import { basketReducer } from "../reducers/reducer";

const BasketList = ({ oneItem }) => {
  const [state, dispatch] = useReducer(basketReducer, { price: oneItem.price, qantity:1  })

  console.log('count', state)

  return (
    <>
      <div key={oneItem._id} className="col-md-4 col-sm-6">
        <div
          className="card"
          style={{ borderColor: oneItem.website ? "" : "red" }}
        >
          <Link to={`item/${oneItem._id}`}>
            <div className="card-header">
              {oneItem.img == undefined
                ? ""
                : oneItem.img.map((e) => (
                    <img
                      key={oneItem._id}
                      style={{ maxWidth: "100%" }}
                      src={"http://localhost:3001/" + e}
                      alt={oneItem.name}
                    />
                  ))}
            </div>
            <div className="card-body">
              <h2>{oneItem.title}</h2>
              <h5>{oneItem.category}</h5>
              <div>{state.price}</div>
              <div>{state.qantity}</div>
            </div>
          </Link>
          <button style={{display: oneItem.price == state.price  ? 'none' : 'block' }} onClick={()=> dispatch({type: 'DECROMENT', payload:oneItem.price})}> - </button>
          <button onClick={()=> dispatch({type: 'INCROMENT', payload:oneItem.price,  qantity:1})}> + </button>
        </div>
      </div>
    </>
  );
};

export default BasketList;
