import { React, useContext } from "react";
import WatchList from "./WatchList";
import { prodactsConext } from "../context/contextData";

const WatchApi = () => {
  const { dispatch, watch, user } = useContext(prodactsConext);

  return (
    <div>
      <h1 className="text-center">Watch List</h1>
      {watch.length ? (
        <div className="container">
          <div className="row">
            {watch.map((el) => {
              return (
                <WatchList
                  key={el._id}
                  product={el}
                  dispatch={dispatch}
                  user={user}
                />
              );
            })}
          </div>
        </div>
      ) : (
        <h3 className="text-center">Watch List is Empty</h3>
      )}
    </div>
  );
};

export default WatchApi;
